import { Component, OnInit } from '@angular/core';
import { FizzBuzzService } from './fizz-buzz.service';






@Component({
  selector: 'app-fizzbuzz',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  seriesFB: any;
  randomNumber!: number;
  limit!: number;

  constructor(private fizzBuzzService: FizzBuzzService) { }

  ngOnInit(): void {
  }

  generateSeries(): void {
    this.fizzBuzzService.getFizzBuzzSeries(this.randomNumber, this.limit).subscribe((series: any) => {
      this.seriesFB = series;
    });
  }



}



<div>

  <span>
    {{randomN}}
  </span>
</div>
<button (click)="generateSeries()">Generate Series</button>
<form>
  <label for="randomNumber">Random Number:</label><br>
  <input type="number" id="randomNumber" [(ngModel)]="randomNumber" name="randomNumber"><br>
  <label for="limit">Limit:</label><br>
  <input type="number" id="limit" [(ngModel)]="limit" name="limit"><br><br>
  <button (click)="generateSeries()">Generate Series</button>
</form> 


<div>{{seriesFB}}</div>
